# -*- coding: utf-8 -*-"""Created on Mon Apr 11 15:40:50 2016@author: Gerard"""#Import modulesimport numpy as npimport matplotlib.pyplot as pltimport seaborn as snsfrom tkinter import filedialogfrom os import listdir, sepfrom os.path import isfile, join#Get files to load from model path directorypath_mdl_out = filedialog.askdirectory( **{'initialdir':    '/Volumes/EXT_DISK/encina/2_data_for_scripts/1_modeling/2_model-verhulst-abr/verhulst-output'} )    files_in_dir_list = [f for f in listdir(path_mdl_out) if isfile(join(path_mdl_out, f)) and f.endswith('.npz')]#Loop over files to extract the steady-state part#### I do it initially in a single file only #####Load data from npz filesperiph_xx = np.load(path_mdl_out + sep + files_in_dir_list[0])       #20 dB SPLperiph_xx_bm = periph_xx['bm_velocity']periph_xx_ihc = periph_xx['ihc']periph_xx_an_high = periph_xx['an_high_spont']periph_xx_an_med = periph_xx['an_medium_spont']periph_xx_an_low = periph_xx['an_low_spont']freq = periph_xx['cf']#Plot the overview plotsplt.figure(1)plt.subplot(211)plt.imshow(periph_xx_bm)plt.subplot(212)plt.imshow(periph_xx_ihc)#Plot the results at cf=2000 Hz